mapper.insert:
INSERT INTO MEMBER
(USERID, TEAMID, NAME, AGE, ROLL, PASSWORD, SSN, GENDER, SUBJECT, PHONE, EMAIL)
VALUES(#{userid}, #{teamid}, #{name}, #{age}, #{roll}, #{password}, #{ssn}, #{gender}, #{subject}, #{phone}, #{email});

mapper.select:
SELECT 
USERID, TEAMID, NAME, AGE, ROLL, PASSWORD, SSN, GENDER, SUBJECT, PHONE, EMAIL
FROM MEMBER

	
		USERID LIKE #{userid}
	
	
		AND PASSWORD LIKE #{password}

mapper.update:
		UPDATE MEMBER SET
 		PASSWORD = #{password},
  	TEAMID = #{teamid},
  	ROLL = #{roll}
		WHERE USERID LIKE #{userid}

mapper.delete:
DELETE 
FROM MEMBER
WHERE USERID LIKE #{userid}
AND PASSWORD LIKE #{password}
